cat > app/models.py <<'PY'\nfrom __future__ import annotations

from dataclasses import dataclass
from datetime import datetime, timezone
from typing import Optional

from flask_login import UserMixin
from sqlalchemy import String, Integer, Boolean, DateTime
from sqlalchemy.orm import DeclarativeBase, Mapped, mapped_column, validates

# Password hashing
from passlib.hash import bcrypt  # requires: passlib[bcrypt]
from email_validator import validate_email, EmailNotValidError  # requires: email-validator


def utcnow() -> datetime:
    return datetime.now(timezone.utc)


class Base(DeclarativeBase):
    """Declarative base for all models."""
    pass


class TimestampMixin:
    created_at: Mapped[datetime] = mapped_column(
        DateTime(timezone=True), default=utcnow, nullable=False
    )
    updated_at: Mapped[datetime] = mapped_column(
        DateTime(timezone=True), default=utcnow, onupdate=utcnow, nullable=False
    )


class User(TimestampMixin, Base, UserMixin):
    """Application user."""
    __tablename__ = "users"

    id: Mapped[int] = mapped_column(Integer, primary_key=True)

    # Store normalized email (lowercased). Unique + indexed for login.
    email: Mapped[str] = mapped_column(String(255), unique=True, index=True, nullable=False)

    # Optional display name / handle
    username: Mapped[Optional[str]] = mapped_column(String(50), unique=True, index=True)

    # Hashed password (bcrypt); never store raw passwords.
    password_hash: Mapped[str] = mapped_column(String(255), nullable=False)

    # Simple flags
    is_active: Mapped[bool] = mapped_column(Boolean, default=True, nullable=False)
    is_admin: Mapped[bool] = mapped_column(Boolean, default=False, nullable=False)

    # ---- Business logic

    @validates("email")
    def _normalize_email(self, key: str, value: str) -> str:
        if value is None:
            raise ValueError("email is required")
        try:
            v = validate_email(value, check_deliverability=False)
        except EmailNotValidError as e:
            raise ValueError(f"invalid email: {e}") from e
        # Use normalized (lowercase + IDNA handling)
        return v.email

    # Password helpers
    def set_password(self, raw: str) -> None:
        if not raw or len(raw) < 8:
            raise ValueError("password must be at least 8 characters")
        self.password_hash = bcrypt.hash(raw)

    def verify_password(self, raw: str) -> bool:
        try:
            return bcrypt.verify(raw, self.password_hash)
        except Exception:
            return False

    # Flask-Login compatibility is provided by UserMixin:
    # - is_authenticated, is_active, is_anonymous, get_id()

    def __repr__(self) -> str:
        return f"<User id={self.id} email={self.email!r}>"


__all__ = ["Base", "User"]
