cat > app/auth.py <<'PY'\nfrom __future__ import annotations

from urllib.parse import urlparse, urljoin

from flask import (
    Blueprint, request, redirect, url_for, flash, jsonify, current_app
)
from flask_login import login_user, logout_user, login_required, current_user
from sqlalchemy import select
from sqlalchemy.exc import IntegrityError

from .models import User

bp = Blueprint("auth", __name__, url_prefix="/auth")


def _is_safe_url(target: str) -> bool:
    # prevent open redirects
    ref = urlparse(request.host_url)
    test = urlparse(urljoin(request.host_url, target))
    return test.scheme in {"http", "https"} and ref.netloc == test.netloc


def _wants_json() -> bool:
    return (
        request.is_json
        or request.accept_mimetypes["application/json"]
        >= request.accept_mimetypes["text/html"]
    )


@bp.post("/register")
def register():
    data = request.get_json(silent=True) or request.form
    email = (data.get("email") or "").strip()
    password = data.get("password") or ""
    username = (data.get("username") or "").strip() or None

    if not email or not password:
        msg = "email and password are required"
        if _wants_json():
            return {"error": msg}, 400
        flash(msg, "error")
        return redirect(url_for("main.index"))

    session = current_app.session  # type: ignore[attr-defined]

    # Check existing user
    existing = session.execute(select(User).where(User.email == email)).scalar_one_or_none()
    if existing:
        msg = "account with that email already exists"
        if _wants_json():
            return {"error": msg}, 409
        flash(msg, "error")
        return redirect(url_for("auth.login"))

    user = User(email=email, username=username)
    try:
        user.set_password(password)
        session.add(user)
        session.commit()
    except IntegrityError:
        session.rollback()
        msg = "username or email already in use"
        if _wants_json():
            return {"error": msg}, 409
        flash(msg, "error")
        return redirect(url_for("auth.register"))
    except ValueError as e:
        session.rollback()
        if _wants_json():
            return {"error": str(e)}, 400
        flash(str(e), "error")
        return redirect(url_for("auth.register"))

    login_user(user)
    if _wants_json():
        return jsonify({"id": user.id, "email": user.email, "username": user.username}), 201

    nxt = request.args.get("next") or "/_dashboard"
    if not _is_safe_url(nxt):
        nxt = "/_dashboard"
    return redirect(nxt)


@bp.post("/login")
def login():
    data = request.get_json(silent=True) or request.form
    email = (data.get("email") or "").strip()
    password = data.get("password") or ""

    if not email or not password:
        msg = "email and password are required"
        if _wants_json():
            return {"error": msg}, 400
        flash(msg, "error")
        return redirect(url_for("auth.login"))

    session = current_app.session  # type: ignore[attr-defined]
    user = session.execute(select(User).where(User.email == email)).scalar_one_or_none()

    if not user or not user.verify_password(password):
        msg = "invalid credentials"
        if _wants_json():
            return {"error": msg}, 401
        flash(msg, "error")
        return redirect(url_for("auth.login"))

    login_user(user)

    if _wants_json():
        return {"ok": True, "id": user.id, "email": user.email}, 200

    nxt = request.args.get("next") or "/_dashboard"
    if not _is_safe_url(nxt):
        nxt = "/_dashboard"
    return redirect(nxt)


@bp.post("/logout")
@login_required
def logout():
    logout_user()
    if _wants_json():
        return {"ok": True}, 200
    return redirect(url_for("main.index"))


@bp.get("/me")
def me():
    if not current_user.is_authenticated:
        return {"authenticated": False}, 200
    return {
        "authenticated": True,
        "id": current_user.get_id(),
        "email": current_user.email,
        "username": current_user.username,
        "is_admin": current_user.is_admin,
    }, 200
